(window.webpackJsonp=window.webpackJsonp||[]).push([[9],{589:function(n,t,r){},614:function(n,t,r){"use strict";r(589)},622:function(n,t,r){"use strict";r.r(t);var e=[{timestamp:"10年",content:"入七玄门 成为墨大夫弟子 修炼《长春功》;\r\n偶得神秘小瓶"},{timestamp:"12年",content:"张铁失踪"},{timestamp:"13年",content:"《长春功》修炼到第三层"},{timestamp:"14年",content:"识厉飞雨 救其性命 因首次出手救人的不良后果 直接导致了以后无利不起早的恶习 为其调配减轻痛苦的灵药;\r\n意外发现神秘液体的功效;\r\n炼丹服食 修炼至《长春功》第四层 五官敏感度有了质的变化;\r\n张铁以曲魂面貌登场;\r\n墨大夫露出獠牙"},{timestamp:"15年",content:"与厉飞雨建立深厚友谊;\r\n《长春功》炼至第五层 获得过目不忘能力"},{timestamp:"16年",content:"《长春功》炼至第六层 习得《眨眼剑法》、《罗烟步》等数种武功;\r\n在与墨大夫的火拼中意外胜出 从余子童处得知修仙界的存在 掌控曲魂;\r\n开始修习简单法术 在七玄门中以韩神医身份超然存在"},{timestamp:"18年",content:"《长春功》炼至第八层 将法武结合;\r\n助七玄门击退野狼帮围攻 杀金光上人并夺其升仙令;\r\n在远处见双亲最后一面;\r\n击杀独霸山庄庄主欧阳飞天与墨氏换取暖阳宝玉 驱除寒毒;\r\n参加太南小会 对修仙界有了简单的认识;\r\n《长春功》炼至第九层;\r\n持升仙令加入黄枫谷 照看马姓老者的百药园"},{timestamp:"20年",content:"《长春功》炼至第十一层;\r\n击杀陆师兄 救出陈巧倩 获得两颗筑基丹;\r\n为筑基而参加血禁试炼 在试炼中与南宫婉有了夫妻之实且收获颇丰（含一银色书页）;\r\n被李化元收为记名弟子 得其赠的《青元剑诀》"},{timestamp:"23年",content:"炼筑基丹二十余颗 成功筑基 为散去药力 将《青元剑诀》炼至第四层;\r\n自己开辟洞府"},{timestamp:"24年",content:"识齐云霄;\r\n得《大衍决》前四层功法;\r\n从李化元处得《凝元功》;\r\n在《凝元功》玉简及银色书页中分别得到《青元剑诀》前十三层功法和青竹蜂云剑炼制之法"},{timestamp:"28年",content:"参加燕家举办的夺宝大会 期间偶遇墨彩环及其母亲严氏;\r\n魔道六宗进攻越国七派;\r\n《大衍决》第一层修成;\r\n在守卫灵脉任务中被魔道袭击 逃跑中偶得血玉蜘蛛卵以及大挪移令;\r\n《青元剑诀》炼至第五层达到筑基中期"},{timestamp:"29年",content:"在前线战场灭杀对方筑基期十几人而小有名气;\r\n应李化元要求保护其师兄后人秦姓大家族;\r\n赴齐云霄之约 委托辛如音修复古传送阵;\r\n保护秦家过程中学会无名敛气法决 撞破黑煞教阴谋 与同门灭杀黑煞教 得虚天殿残图;\r\n曲魂被御灵宗结丹修士夺舍 韩立差点成其灵兽腹中餐 灭掉修士元神得其法宝绿煌 尔后用大阵杀掉修士的灵兽 得其培育灵虫之心得;\r\n七派因灵兽山倒戈而大败 六派迁移中韩立趁乱遁走;\r\n齐云霄被付家人杀死 辛如音修复好古传送阵并希望韩立有能力之后灭付家一族;\r\n修复古传送阵之时 因材料不足去采购 无意中碰到'南宫屏'被魔道追杀 将其救下 不想其法力却被南宫吸走 修为降至炼气三四层;\r\n修复好古传送阵 传送至乱星海;\r\n定居魁星岛"},{timestamp:"37年",content:"修成《大衍决》第二层;\r\n修炼身外化身秘术;\r\n之后二十余年在小寰岛苦修 修为提升至筑基顶峰"},{timestamp:"57年",content:"为降尘丹应六连殿之邀出海捕妖 碰巧撞破正魔两道阴谋而被结丹修士古长老追杀 趁其修为下降将其击杀 得降尘丹、法宝混元钵;\r\n在海猿岛准备结丹"},{timestamp:"61年",content:"曲魂结丹成;\r\n意外得噬金虫幼体;\r\n定居天星城 调教灵兽 制造傀儡、符箓"},{timestamp:"63年",content:"在凝翠岛利用霓裳草杀妖取丹"},{timestamp:"68年",content:"利用妖丹炼制各种丹药 持续两年"},{timestamp:"70年",content:"闭关冲击结丹期"},{timestamp:"130年",content:"进入结丹期 期间噬金虫进化 《大衍决》炼至第三层 《青元剑诀》炼至第七层;\r\n为得天雷竹受雇妙音门 落入极阴岛陷阱 侥幸逃脱;\r\n成妙音门名义长老得天雷竹"},{timestamp:"132年",content:"培育出第一根万年天雷竹——金雷竹;\r\n开始研究炼器以及阵法 培育金雷竹,开店'青竹小轩'入世二十余年"},{timestamp:"152年",content:"青竹蜂云剑成;\r\n受金青所邀探索古修遗址 与石蝶合力研究破阵"},{timestamp:"153年",content:"破阵后释放玄骨 靠过人实力心智将其惊退;\r\n虚天殿现世 乱星海众修士探宝;\r\n带元瑶、紫灵闯过第一关;\r\n被玄骨算计 靠心计赢得胜利 得九曲灵参;\r\n第二关救元瑶 得啼魂;\r\n被极阴等人胁迫进入内殿 助取虚天鼎 即将成功之际 被星宫长老偷袭 差点死去;\r\n正魔为补天丹火拼之际与玄骨一起取出虚天鼎 在一番争斗后 玄骨身死 得虚天鼎及乾蓝珠、《玄阴经》、补天丹;\r\n看到元瑶裸浴 和其合力破阵后得万年灵乳及养魂木根部;\r\n逆星盟成立;\r\n为了摆脱大战传送至外星海奇渊岛"},{timestamp:"154年",content:"青竹蜂云剑加入炼晶;\r\n目睹龟妖化形雷劫;\r\n服用五色珠子"},{timestamp:"155年",content:"五色珠子炼化完毕 吸纳天地灵气和转化灵力比以前稍快;\r\n做好万全准备 再次出海补妖"},{timestamp:"158年",content:"为一只七级妖兽斩杀结丹数名 被称'虫魔'而不自知"},{timestamp:"160年",content:"奇渊岛深渊妖兽暴乱"},{timestamp:"162年",content:"七年内斩杀妖兽数百 开始炼丹"},{timestamp:"165年",content:"炼丹术提升 得各种丹药,服药炼功十五六年"},{timestamp:"180年",content:"《青元剑诀》炼至第八层 进入结丹中期,此年前后 奇渊岛被妖兽攻下。之后又闭关二十多年"},{timestamp:"205年",content:"赠予公孙杏宝物;\r\n改头换面参加妙音门举行的秘市 遇文思月 将其救出 从其处得知千叶露消息;\r\n得梵圣真片;\r\n闯风希洞穴 得千叶露;\r\n进阶结丹后期 尔后被迫协助风希炼制风雷翅"},{timestamp:"206年",content:"被下风灵劲 诱三妖喝下掺杂神秘绿液的万年灵乳;\r\n抢夺风雷翅 灭杀毒蛟;\r\n被风希一路追杀;\r\n炼化风雷翅;\r\n为传至内星海替范静梅除掉云啸天 被妙鹤及风希追杀 传送至内星海皇明岛;\r\n救元瑶 得赠万年灵乳、阵法玉简;\r\n守护元瑶施还魂秘术 与温天仁大战 被鬼雾吸入 传送到阴冥之地 结识梅凝;\r\n习得妖族文字 靠梅凝的通灵之气短暂获得灵力 收走大量'沉水' 遇天符门第五十七代掌门 受其所托将降灵符炼制之法带回天符门 啼魂进阶 斩杀阴兽 得兽晶 攀爬暴风山 杀温天仁 带紫灵、梅凝出阴冥之地;\r\n以炼气期修为拜入落云宗 驱除风灵劲 炼化鸣魂珠"},{timestamp:"207年",content:"抓捕雪云狐过程中碰见尸魈 得封印尸魈魂魄盒子 银月获得肉体且成为青竹蜂云剑器灵;\r\n啼魂进阶"},{timestamp:"208年",content:"参加试剑大会 接近灵眼之树 正魔两道为救活玄天仙藤抢夺灵眼之树 韩立趁乱取得定灵丹、灵眼之树根部及定灵丹和明清灵水配方 之后二十多年 《大衍决》第四层、《青元剑诀》第九层相继炼成"},{timestamp:"230年",content:"进阶元婴;\r\n正式加入落云宗成为长老 纳慕沛灵为妾;\r\n炼制明清灵水 开始炼化乾蓝冰焰"},{timestamp:"231年",content:"擒至木灵婴;\r\n灭付家满门 拜祭辛如音与齐云霄 得《玄牡化婴大法》;\r\n移走昔日洞府内的灵眼之泉 了却尘世情缘;\r\n收柳眉为记名弟子 得御灵宗驱虫秘术、六翼霜蚣卵"},{timestamp:"232年",content:"参加天南交易会 用魂石从天晶真人处交换古傀儡炼制之法;\r\n受南陇侯之邀探索苍坤上人遗址 路遇慕兰法士大军 九人分头逃命;\r\n灭追杀之人肉身 被元婴逃脱 得千重峰、御风车;\r\n苍坤遗址内被王天古等人算计 与南陇侯一起逃出;\r\n返回途中救下雷万鹤等五人 得知南宫婉将要嫁人的消息;\r\n再见南宫婉 带南宫加入落云宗;\r\n拒绝令狐 但答应助黄枫谷三次;\r\n与法士斗法中展露实力 在元婴后期修士手下逃得性命 声名鹊起;\r\n见紫灵 谋划坠魔谷之行;\r\n答应联盟出战得庚精、玄天仙藤;\r\n炼制降灵符;\r\n参加边界之战 斩杀阴罗宗宗主夫人 突兀人占领大草原 天南不得以与慕兰族和解"},{timestamp:"233年",content:"南宫婉被下封魂咒 青竹蜂云剑加入庚精;\r\n斩杀阴罗宗四长老 得鬼罗幡;\r\n将至木灵婴炼化为第二元婴 将尸魈炼成天绝魔尸;\r\n前往极西之地"},{timestamp:"235年",content:"从极西之地返回 带回大衍神君;\r\n为解南宫之咒 探坠魔谷 与南陇侯、鲁卫英一起得火蟾兽妖丹、七焰扇炼制之法及一些灵料;\r\n斩杀孽形兽 得其内丹 带紫灵取灵烛果 炼造化丹服用;\r\n血焰被放出 与众修士大战 天绝魔尸遗留在坠魔谷;\r\n斗法中打破空间裂缝 与血焰主魂同时跌入灵缈园遗址 两把青竹蜂云剑被血焰分身夺得 血焰主魂被禁制灭杀;\r\n在灵缈园遗址内炼丹修炼"},{timestamp:"262年",content:"进阶元婴中期;\r\n从灵缈园遗址逃出;\r\n击败古剑门大长老 落云宗成为溪国第一大派;\r\n乾蓝冰焰全部炼化 培炼虚天鼎;\r\n潜入魔气深渊放阵盘 得灵岛 得四枚魔髓钻 修复鬼罗幡"},{timestamp:"266年",content:"第二元婴成 为解南宫婉的封魂咒、解决身上煞气、寻找珍稀材料出发前往大晋;\r\n在天澜草原被突兀人发现携有众多噬金虫 被追杀 引动煞气反噬 无奈下修炼《五鬼锁神大法》;\r\n假扮低阶仙师穿草原 救下大晋冯家弟子 得冯家密窟钥匙、苦毒;\r\n被天澜兽及突兀仙师追杀 用分身之计摆脱追杀 第二元婴重伤与本体失去联系 收天澜兽 用假死之术冰封身体"},{timestamp:"267年",content:"顺河漂入大晋 银月亦陷入昏迷;\r\n被曹梦容所救 恢复伤势 助其解决争端;\r\n参加参王大会 得金刚舍利、晶化妖丹;\r\n探索冯家密窟 得舍利子、《明王诀》 救下炫烨王 得天尸珠;\r\n炼化天尸珠"},{timestamp:"268年",content:"炼成《明王诀》第一层;\r\n加入皇清观 学习炼器技艺、炼制晶化妖丹飞针、修炼《明王诀》第二层"},{timestamp:"269年",content:"《明王诀》第二层修成 离开皇清观;\r\n替大衍神君四处搜刮傀儡材料"},{timestamp:"270年",content:"得昊阳鸟翎、土甲龙;\r\n偶遇曹梦容;\r\n晋京内换得天机府 被天澜圣女及阴罗宗之人撞见 在大衍神君帮助下逃过一劫 被肖老儿以此事敲诈 灭杀肖老儿 得寒髓;\r\n参加晋京拍卖会 之后在富姓老者指引下参加地下拍卖会 凑齐傀儡材料 得庚精 逃离古魔追杀;\r\n到天符门送还降灵符炼制之法 遇假扮低阶弟子的向之礼 学会许多符箓炼制之法 助天符门解决争端 得化灵符;\r\n灭蛟得妖兽材料;\r\n在海岛炼制傀儡"},{timestamp:"271年",content:"炼成可比元婴后期傀儡 击败苦竹老人 得乌凤之翎;\r\n大衍神君进入轮回 留下《大衍宝经》;\r\n收服土甲龙"},{timestamp:"273年",content:"炼制三焰扇;\r\n为培婴丹与富成等四人闯阴阳窟 与此同时 叶家开始布阵破昆吾山禁制;\r\n阴阳窟内发现阴芝马 追逐中遇银翅夜叉 夜叉大战五人 元姓大汉、常芷芳身死 富成、白瑶怡被困 韩立一人与银翅夜叉周旋 叶家破禁后被传送至昆吾山 破禁异象震惊众修;\r\n得三颗培婴丹 探索昆吾山 得太阴真火、火鼎;\r\n在昆吾殿内收复圭灵 斩杀阴罗宗大长老 得封魂咒解咒之法、两杆鬼罗幡、五子同心魔;\r\n八灵尺现世 众修为宝联手破禁;\r\n元刹冲破封印 与珑梦斗在一起;\r\n斩杀血焰 取回两把青竹蜂云剑 众修被元刹用黑风旗关入空间壁障内 众修联手破禁而出 突兀大仙师被万年尸熊偷袭 韩立为得成熟噬金虫培育之法出手将其救下 给其下叱念真雷禁制;\r\n被传送至第九层空间 与众人合力对付元刹 元刹神念降临 险些被杀 被玲珑所救 噬金虫咬断石柱打破法阵 元刹神念消失 之后元刹分神引爆黑风旗将封印彻底打破 真魔气滚滚涌出 众人暂时逃离第九层;\r\n封印银月关于小瓶的记忆 返回第九层助玲珑夺回躯体 玲珑返回灵界;\r\n得黑风旗残骸、八灵尺以及空间节点信息;\r\n寻地潜修"},{timestamp:"285年",content:"十余年时光将所得宝物尽数炼化如意;\r\n为玄冰花拜访白瑶怡 对于出现在北极小夜宫的虚灵殿震惊异常;\r\n答应助寒骊上人冲击化神 得精炼寒焰的秘术;\r\n得花炼丹 斩杀八级妖兽救下白瑶怡;\r\n同时 为了空间节点 万妖谷与冰海妖族进攻小夜宫;\r\n与龙夫人、摩鸠、白梦馨、欧阳助寒骊冲击化神 目睹寒骊进阶失败 拒绝交出虚天鼎 大战起 韩立将五人灭杀收取五种寒焰 用阴灵水取下万年玄玉 太阴真火吸收玄玉寒气;\r\n虚灵殿被众妖攻入 斩杀不知名妖兽 得破灭法目 为通过传送阵 与冰凤、车老妖化身大战 势均力敌 意外下传送至虚天殿;\r\n因修为不足无法出殿 在殿内潜修"},{timestamp:"345年",content:"修炼至元婴中期巅峰"},{timestamp:"365年",content:"突破至元婴后期 与冰凤合力打开虚天殿禁制传送至乱星海;\r\n图谋高阶灵石欲传送至外星海被双圣发现 温青震惊韩立修为而放行;\r\n出手救下田琴儿 将阵法典籍留给田参悟;\r\n偶然撞见罗睺与游天鲲鹏的战斗 得游天鲲鹏翎羽;\r\n斩杀妙鹤 得数百高阶灵石 搜魂得极阴消息;\r\n禁锢极阴交予蛮胡子 换取蛮胡子《托天魔功》及洞府地址;\r\n妖兽为极品灵石围攻星宫及逆星盟之人 韩立坐收渔利 得极品灵石;\r\n传送回内星海 拒绝与凌玉灵结为道侣 但答应双圣助玉灵三次以换取冲击化神方法;\r\n斩杀金花老祖得金阙玉书残页 取出蛮胡子洞府藏宝;\r\n从天澜圣兽处学会银蝌文;\r\n收田琴儿为徒;\r\n返回天南 访辛如音旧居"},{timestamp:"366年",content:"返回落云宗 青竹蜂云剑全套加入庚精、玄玉;\r\n举办大修士典礼 震惊天南;\r\n将鲲鹏羽炼入风雷翅中 提炼火灵丝、炼化破灭法目;\r\n坠魔谷发现第二元婴踪迹 救出慕沛灵等人;\r\n在万丈魔渊擒第二元婴 抹去其神识;\r\n助天澜兽渡过化形雷劫 期间得帝流浆 斩杀变异独角蚯得吸灵材料"},{timestamp:"368年",content:"举办双修大典 万修朝贺;\r\n助鬼灵门摆脱灭门危机换取聚灵珠"},{timestamp:"493年",content:"闭关一百二三十年 期间灵目神通大进 炼制炼化木生珠 五子同心魔融合五种寒焰 《大衍决》炼至最后一层 《明王诀》炼至第三层 学会《疾风九变》收到凌玉灵传信 提前出关;\r\n收石坚为徒 传大衍神君衣钵;\r\n传至乱星海 答应对付万天明以交换《 元磁神光》 及元磁山 偷杀当初虚天殿内偷袭他的星宫长老;\r\n参加星宫之战 斩杀六道极圣 助星宫夺取星海控制权;\r\n收元磁山失败 成为星宫客卿长老;\r\n返回落云宗 闭关五十年 "},{timestamp:"543年 ",content:"修至元婴后期巅峰 突破化神失败;\r\n前往大晋 向天机阁取得芥子空间炼制秘术 "},{timestamp:"544年 ",content:"斩杀阴罗宗宗主 得灭仙珠;\r\n灭阴罗宗数名元婴修士 夺取数杆鬼罗幡;\r\n被风老怪追杀 向之礼解救 拿出空间节点资料;\r\n参加呼老魔纳妾典礼 救下紫灵;\r\n用万年玄玉交换魔元丹;\r\n与紫灵春风一渡 "},{timestamp:"545年 ",content:"捕捉太阳精火 返回天南 "},{timestamp:"580年",content:"炼化太阳精火 炼制回阳水;\r\n在坠魔谷炼制赤魂幡 将灵缈园遗址炼制为芥子空间;\r\n用回阳水浇灌玄天仙藤 开始修炼《元磁神光》"},{timestamp:"590年",content:"《元磁神光》第一层炼成 玄天仙藤复活;\r\n前往星宫 取得元磁山 打听金蛟王下落"},{timestamp:"592年",content:"斩杀金蛟王、风希 得金属性妖丹、《淬骨诀》、移植龙鳞果方法 移植龙鳞果;\r\n返回天南"},{timestamp:"593年",content:"将南宫婉等人送至乱星海 自己则在坠魔谷苦修 之后二百多年间炼化元磁山 修成《 元磁神光》 进阶化神 "},{timestamp:"893 年 ",content:"修炼至化神初期顶峰《 青元剑诀》 大成;\r\n在五龙海找到向之礼等人进入的空间节点 发现开始有不稳迹象;\r\n传信让田琴儿来加强封印 "},{timestamp:"894年 ",content:"田琴儿通过阵法加强节点封印 "},{timestamp:"900年 ",content:"冰凤现身 亦打算通过空间节点;\r\n开始解除虚天鼎对天澜兽束缚;\r\n在人界大肆搜寻空间、 防御类宝物以及秘术 "},{timestamp:"970年 ",content:"期间服用龙鳞果 修炼《 淬骨诀》《 明王诀》 炼至第四层;\r\n在天沙大陆与天澜兽分道 "},{timestamp:"972年 ",content:"回到五龙海 南宫婉一行人皆在此地 与冰凤约定同闯空间节点 获南宫婉赠予的六丁天甲符;\r\n与南宫婉寸步不离 珍惜在一起的时光 "},{timestamp:"975年",content:"玄天仙藤开花结果 "},{timestamp:"976年 ",content:"得玄天仙藤果实 "},{timestamp:"1002年 ",content:"将元婴后期傀儡、 三焰扇、 两只乾蓝鼎交予南宫婉 与冰凤互下禁制后联手闯入空间节点;\r\n进入灵界 八灵尺、 六丁天甲符毁于空间节点 禁制反噬 将元婴精元用来修复肉体 百年内不能动用法力神念 将宝物埋藏在青罗沙漠;\r\n被天东商号所救 到达天远城 购买书籍 大概了解灵界 制订修炼计划;\r\n经历天远城被大兽潮攻破 救下黛儿;\r\n为九玄明玉潭突破瓶颈功效助金玉宗修士取宝;\r\n在虞阳城潜修 "},{timestamp:"1042年 ",content:"《金刚诀》 炼至第七层瓶颈 离开虞阳城前往青罗沙漠取宝 十二条六翼霜蚣只剩一条且因法力大增摆脱韩立控制;\r\n经过数年时间赶至落日城 "},{timestamp:"1047年 ",content:"为突破瓶颈前往落日之墓;\r\n卷入灵族神血的争夺 灵族噬炎欲灭杀韩立卧底人族 反被太阴真火吞噬 真火进阶为噬灵天火;\r\n暂时拥有法力 得灵族捕获的变异豹麟兽 从三位炼虚期手中夺得灵族神血;\r\n隐迹 "},{timestamp:"1048年 ",content:"躲避追查 "},{timestamp:"1049年 ",content:"在落日之城附近小城采购 ,数年后服用灵族神血 闭关。百余年后《金刚诀》大成、神通尽复"},{timestamp:"1149年",content:"渡飞升修士特有的两色雷劫 被天渊卫所救 至天渊城 任丙五十六队队长;\r\n逐渐熟悉灵界的环境;\r\n争夺灵地 啼魂进阶 尝试将神秘绿液滴在玄天果实上 触动混沌万灵榜变化;\r\n带领小队巡逻浮黎沼泽;\r\n在坊市购得雷纹树枝 潜心研究;\r\n参加天渊城拍卖会 拍下墨麒麟鳞片;\r\n参悟雷纹 炼制雷纹战甲;\r\n炼制太一化清符;\r\n争夺灵地之人来灵地寻宝 与玉骨人魔相斗引发空间风暴;\r\n得金阙玉书外页——《百脉炼宝决》 经过慎重考虑 决定修炼《梵圣真魔功》;\r\n边完成任务边修炼"},{timestamp:"1199年",content:"进阶化神中期"},{timestamp:"1209年",content:"六十年的任务中常常有所斩获 名闻方圆,用万年灵药与妖族女子交换灵草种子;\r\n参加飞升修士聚会 得知异族攻城概率较高;\r\n噬金虫进化为成熟体;\r\n为真蟾液及灭尘丹接下木族任务 与陇东、 叶颖、 筱虹及李姓妖修同入蛮荒世界 从巨人与蜥蜴兽手下抢夺芝龙果;\r\n途中被影族埋伏 李姓身死 四人分散逃走 噬金虫惊退来敌;\r\n潜入木族 被合体期木瑞发现 人族卧底叶楚将木瑞暂困 其他人各自逃命;\r\n靠豹麟兽寻得叶颖 将木族情报发回天渊城 完成任务 得解除灭尘丹禁制法诀 "},{timestamp:"1210 年 ",content:"返回途中 陇东用计夺取叶颖的天凤灵血 韩立不得已出手 击败筱虹得其储物镯 击退陇东 助叶颖夺回灵血 真龙灵血也被收入囊中 用真龙灵血交换天凤之翎 趁机留下部分真龙天凤灵血 与二女分开;\r\n被木瑞追至 噬金虫退敌;\r\n寻地回复;\r\n风雷翅中加入天凤之翎 进阶为灵宝 "},{timestamp:"1211年 ",content:"与飞升修士围剿真蟾兽 被夜叉族部队围攻 趁乱灭杀真蟾兽并带走尸体 远离夜叉族围攻;\r\n与肖姓女子先后逃出却撞上夜叉王 肖仙子使用金篆文传送阵 受夜叉王影响 二人分开;\r\n传送至赤魔海黑冥岛 赠小鹿开灵丹 开始苦修 "},{timestamp:"1271年 ",content:"黑冥岛众妖兽攻打韩立洞府 损伤惨重 "},{timestamp:"1301年",content:"冲击化神后期失败 结束闭关;\r\n噬金虫吞噬不知名石头 陷入沉睡;\r\n得不知名石头;\r\n用木玲花与黑冥岛妖物交换得金髓晶虫、金母珊瑚沙"},{timestamp:"1306年",content:"部分金髓晶虫、全部金母珊瑚沙用来锻体;\r\n噬金虫变异;\r\n为返回风元大陆 尾随收取贡品的天鹏族人;\r\n意外卷入战斗 击退赤融族人;\r\n进入天鹏族圣城;\r\n风雷翅引动鲲鹏圣灵躁动 惊动天鹏族大长老 受邀参加天鹏族圣子试炼;\r\n融合鲲鹏之血、炼化鲲鹏舍利 得《惊蛰诀》口诀 进阶化神后期;\r\n为青罗果帮鱼老板收服雷兽 得青罗果果核;\r\n随天鹏族参加地渊试炼 得《四煞化甲术》秘术;\r\n进入地渊 因破坏木青收集血食被盯上 先后数波击退四妖王手下;\r\n豹麟兽进阶化神;\r\n助天鹏族圣子白璧和雷兰完成试炼;\r\n木青亲自前来捉拿;\r\n因身怀辟邪神雷被四妖王视为一大臂助而免去杀身之祸 但也被四妖王中下印记 遇元瑶;\r\n木青传授《祭雷术》上半部"},{timestamp:"1307年",content:"渡小天劫 收取天劫青雷 炼制雷珠;\r\n协助地血炼制傀儡 得地血的傀儡秘术、歌舞灵侍及地渊特有的傀儡材料和一些珍稀药材 因听取地血讲述符阵之道而有所领悟;\r\n炼制甲元符、九宫天乾符"},{timestamp:"1308年",content:"掌握《祭雷术》上半部;\r\n答应助木青在冥河之地取宝 得五色孔雀真血"},{timestamp:"1310年",content:"将五色孔雀真血彻底融入体内;\r\n与元瑶二女商议逃脱办法"},{timestamp:"1312年",content:"进入冥河之地 协助四妖王打通冥河通道;\r\n随四妖王大军一路向目的地前进;\r\n被围困鬼雾中 乱走下救元瑶二女 在二女帮助下走出鬼雾 遇木青、鬼婆与蜉蝣族人寄付的傀儡对峙 六足、地血破掉鬼雾 灭杀大部分傀儡;\r\n在阴骨山脉前假装被六足控制引开冥雷兽 不敌冥雷兽 用九宫天乾符将其困住 之后隐匿 在冥雷兽之后返回阴骨山脉;\r\n两头冥雷兽与三妖王大战 蜉蝣族人趁机偷袭 抓住一线机会救走元瑶二女 二女施法驱除其体内印记 逃出生天;\r\n逃跑中遇冥雾 被传送至青元子洞府 用金雷竹叶子换得《新青元剑诀》以及青竹蜂云剑提炼方法 答应青元子替其收集材料以换取冥河神乳 出冥河之地;\r\n传送到黑冥岛黑冥雾中 被皓兽攻击 反杀;\r\n出黑冥雾 开始将不知名石头炼入元磁山 种剑"},{timestamp:"1415年",content:"进阶炼虚;\r\n元磁神山炼成 将真龙、 天凤真血彻底融入体内;\r\n助黑冥岛妖兽驱除奴痕得血杏 "},{timestamp:"1420年",content:"开始炼制腾龙丹 惊叹丹药成功率之低"},{timestamp:"1460年",content:"观看异族与骨鸟大战 趁机逃遁;\r\n青竹蜂云剑提纯完毕 参悟春黎剑阵;\r\n玄天果实被血祭召唤 刺破韩立手掌 吸取其精血 韩立无意中挥出一剑 被吸走十之八九的精血法力及神识 虽打破传送 但仍被传送至雷鸣大陆火瑚群岛 伤势严重 玄剑附臂;\r\n服用火阳族烈阳丹 恢复大半修为 替火阳族解决灭族危机 噬灵火鸟获灵漩邪光神通 得可屏蔽神念之黑纱;\r\n从青筱处得知某海兽吞噬了传往雷鸣大陆的法阵 决定联手灭兽"},{timestamp:"1461年",content:"修为尽复 前往参加聚会;\r\n不知名飞蛾肆虐 将其斩杀;\r\n骷髅哥初现;\r\n为空云晶寻海兽;\r\n用飞蛾尸体炼制晶鳞盾、成批飞刀;\r\n找寻中得海兽蜕皮 炼制九宫天乾符、甲元符、太一化清符"},{timestamp:"1465年",content:"发现海兽与血焰虾对峙 击杀二兽"},{timestamp:"1467年",content:"修复好传送阵 传送至雷鸣大陆绿光城 恰角蚩族攻城;\r\n受委托将广寒令送至天云十三族 斩杀角蚩族奸细得另一枚广寒令;\r\n被角蚩族半步合体追上 将来敌惊退 跑路;\r\n逃跑途中遇角蚩族围攻傀儡大师甲天木 本想绕过 被甲天木看破 无奈下屠戮角蚩族人 救下甲天木;\r\n在甲天木带领下到达云城;\r\n偶遇向之礼;\r\n与纤纤交换得破损天外魔甲 为修复魔甲 相约入魔金山脉;\r\n将灵侍送予甲天木得跨大陆传送阵消息 得甲天木送予的通灵傀儡 起名娃娃;\r\n无意中激发广寒令 为使用跨大陆传送阵 与天云十三族达成协议;\r\n炼腾龙丹 闭关"},{timestamp:"1468年",content:"偶遇酷似陈巧倩女子;\r\n与彩流罂、段天刃两位合体达成协议;\r\n参加天云四族拍卖会 拍下瑕疵琉璃天火液;\r\n与纤纤、越宗进入魔金山脉 灭杀尾随的多眼魔;\r\n灭人面鹫 噬灵天火吞噬金乌真血;\r\n斩杀重伤的合体级魔猿得其魔核、躯体以及玄天残刃 梵圣法相受创 得金阙玉书外页——《玄天炼器术》;\r\n啼魂变身刑兽斩杀被天外魔君分魂寄付的穴灵 吞噬麒麟本源 陷入沉睡;\r\n返回途中被五泣、英堂、青针三魔追杀 因无法摆脱 将来敌瞬杀 继续跑路;\r\n将被彦姓老者、血瑛等三人追逐的芝仙收入囊中 力压三人 将绿发异族斩杀 此时铁翅魔化身追来 转身跑路;\r\n把铁翅魔化身带远 将其斩杀得璃水珠;\r\n与纤纤逃出魔金山脉;\r\n炼化魔猿尸体得山岳巨猿真血;\r\n将璃水珠送予娃娃;\r\n与芝仙达成协议;\r\n辅助纤纤修补天外魔甲;\r\n与许老怪交换得炎金之精"},{timestamp:"1469年",content:"芝仙兵解 临去前告诉韩立芝仙肉身作用——炼制身外化身和替身 并且助九曲灵参早日开启灵智;\r\n凝练法相金身"},{timestamp:"1470年",content:"九曲灵参开启灵智 取名曲儿"},{timestamp:"1472年",content:"融入山岳巨猿灵血 炼制元磁极山;\r\n广寒界开启 得翁姓大乘赠送的天罡印 进入广寒界;\r\n与柳水儿、石昆在路途或躲避或灭杀界中各种古兽到达目的地 破禁后收太乙青山;\r\n先一步到达宫殿 晶鳞盾被禁制所毁 得黑幽冰香、万剑图等各种宝物 在宫殿药园内得红罗果和蚀毒草及不知名各种仙药;\r\n与二人汇合 合力取宝 得葫种剑 曲儿趁机拿一粒虚灵丹 赠曲儿宝物;\r\n依靠破灭法目进入屏中洞天 触发星空图禁制 淬炼梵圣金身 本体及第二元婴尽皆达到炼虚后期巅峰 差点爆体而亡 彷徨无助之际 玄天斩灵剑护主 骷髅哥二现;\r\n角蚩族触动禁制 宫殿分崩离析 韩立三人分头而逃;\r\n无法摆脱蜀黍 抢夺混元尺 变身巨猿将其斩杀;\r\n斩杀飞虹鱼得其内丹数万;\r\n收集石茧族所需矿石 曲儿发现灵药并采集;\r\n两名天云人月仙子、胥姓老者被四名戎族人追杀 连累曲儿 韩立出手灭敌 月仙子主动交出九焰草 韩立送二人丹药;\r\n从二人口中得知《炼神术》逆天功效 前往抢夺;\r\n斩杀数名戎族人之后取得《炼神术》 从被杀之人身上得玄光刃;\r\n将《炼神术》复制给二人 习得金篆文;\r\n临出广寒界前 斩杀油尽灯枯的王级暗兽 将内丹送豹麟兽吞噬"},{timestamp:"1473年",content:"出广寒界 云城被角蚩族攻入 失守;\r\n受翁大乘所托将信物送予冰魄仙子 得其所赠材料;\r\n到达伏蛟城 用仙界灵药与千机子、段天刃、彩流罂交换物品;\r\n采购雷鸣大陆特产;\r\n斩杀附身向之礼的血影;\r\n返回风元大陆"},{timestamp:"1575年",content:"百余年横穿风元大陆 屡次犯险 但收获颇丰;\r\n识雷云子'交流'雷电心得,斩杀石元龟救下四名人族 返回天渊城 依旧没有南宫婉消息;\r\n在天渊城采购材料准备闭关冲击合体;\r\n《炼神术》 第一层大成;\r\n拒绝谷家拉拢;\r\n为冲击合体做准备 "},{timestamp:"1576年 ",content:"进阶合体期 巩固修为 安排接下来的修炼之路 "},{timestamp:"1577年 ",content:"拒绝天渊城戚煦冰长老邀请 答应谷家替其出手一次换取部分真灵血脉炼化之术 "},{timestamp:"1580年 ",content:"委托所救四人打探南宫婉消息;\r\n骷髅哥三现;\r\n拒绝金越邀请;\r\n将翁大乘所托之物送至许家 助许家唤醒血魂 归还虚天鼎 得魔玉花;\r\n游历天元境 "},{timestamp:"1587年",content:"偶遇器灵子、 海大少 对二人传承法器及可消失灵根感兴趣 出手救下二人 一同前往参加万宝大会;\r\n救下白果儿 识天元圣皇;\r\n为了将来得到昊阴寒魄山 收白果儿为记名弟子;\r\n结识万骨真人 得知黑域交换会的存在;\r\n炼制红罗仙酒;\r\n再见叶颖 得知魔劫消息 重新制订修炼计划;\r\n收器灵子、 海大少为记名弟子;\r\n参加万宝大会 再遇黛儿 结识黑凤王;\r\n拍下泣灵血木;\r\n救下器灵子 与陇家老祖交手;\r\n与天狐族秦素儿交换物品;\r\n参加黑域交换会 换取不少珍惜物品以及冰凤;\r\n炼制化劫傀儡、 太乙青山 "},{timestamp:"1591年 ",content:"以谷家客卿身份参加真灵大典 得谷家所赠有关真灵之血的典籍;\r\n陇家老祖邀请魔界之行 ,之后近百年韩立一直在三皇七妖之地游历 寻找南宫婉失败后回天渊城附近闭关一百年"},{timestamp:"1787年",content:"修炼《万毒混元身》最后一层;\r\n继续闭关"},{timestamp:"1907年",content:"进阶合体中期 指点三名弟子修炼"},{timestamp:"1910年",content:"噬金虫再次变异 出发前往冥河之地;\r\n骷髅哥四现"},{timestamp:"1990年",content:"再临天鹏族 在金悦帮助下进入地渊;\r\n被鱼老板跟踪 一起传送至冥河之地 在啼魂帮助下斩杀鱼老板 得五色孔雀真血 啼魂飞升真仙界;\r\n送予青元子其所需材料 接受青元子指点"},{timestamp:"1991年",content:"随青元子见金焰候 得青元子所承诺的冥河神乳 用五千成熟体噬金虫与蜉蝣族太上长老虚灵交换得噬金虫王培育之法及冥河神乳;\r\n返回人族"},{timestamp:"2291年",content:"魔斑降世;\r\n骷髅哥恢复法体;\r\n入驻天渊;\r\n见陇家老祖 同意共往魔界 与其交易得龙灵丹及擎天巨舟;\r\n魔族开始进攻天渊城"},{timestamp:"2292年",content:"受天渊城委托 往助倚天城防守 与银光同行;\r\n斩杀力尊者孙子 惹怒力尊者;\r\n被力尊者、米尊者、蓝尊者追至 韩立将三魔先后斩杀 得镇魔锁;\r\n研究镇魔锁 识车骑恭;\r\n参加倚天大战 斩杀烟尊者、阴刹 血光附体阳刹;\r\n被血光追杀 从车骑恭处得知两件玄天残宝的弱点 与血光大战 将血光逼退 万剑图被毁 得紫言鼎;\r\n巧遇许芊羽;\r\n助圣岛使者雨啸天收服黄粱石灵;\r\n提取镇魔锁中混沌二气 被元刹化身发现并追杀;\r\n路上元刹与血光联手;\r\n瞬杀路上所有堵截魔族 仓皇逃命;\r\n为摆脱追兵 将曲儿及第二元婴同时放出 引诱二魔追击 本体毫无规律的逃窜"},{timestamp:"2293年",content:"逃往蛮荒世界;\r\n击杀古兽 以妖丹炼药用来回复法力"},{timestamp:"2294年",content:"路遇被宝花追逐的雷云子 玄天残刃被宝花夺走;\r\n与雷云子施展双重雷阵传走;\r\n寻地恢复 用混沌二气与车骑恭、风邪换得混沌二气炼化之法;\r\n潜入魔界 将镇魔锁丢入地下河流;\r\n一边赶往天渊城 一边炼化混沌二气;\r\n豹麟兽进阶合体"},{timestamp:"2295年",content:"返回天渊;\r\n解决青龙;\r\n进阶合体后期;\r\n炼制天戈符;\r\n参加天渊大战 施展涅盘圣体 斩杀魔族第一魔尊匈魔尊 元气大伤 噬金虫群吞噬血光化身;\r\n闭关养伤 期间第二元婴及曲儿返回"},{timestamp:"2296年",content:"魔界之行提前;\r\n与陇家老祖、晖姓长老、叶家长老、林家长老、灵族千秋、金鼓、白戚、藏形、止水汇合;\r\n炼化紫言鼎;\r\n进入魔界"},{timestamp:"2297年",content:"遇草原兽潮 躲入血鸦城;\r\n得魔气与灵气融合的异魔金;\r\n炼化异魔金所含魔灵力 体内现不知名绿气;\r\n参加血鸦城拍卖会 拍下通玄药灵遗骨 让曲儿控制芝仙灵躯炼化;\r\n抢夺含有泣灵秘藏图的四块圣砖;\r\n灵族五人提前离开 人族五人分别从吸魔蚁群中突围"},{timestamp:"2298年",content:"一年内 在魔族的蛮荒之地发现许多珍稀材料和灵药;\r\n重新修炼《百脉炼宝决》 修炼《炼神术》第二层;\r\n救幻夜城白家之人;\r\n抵达幻夜城;\r\n因发现朱果儿修炼《素女轮回功》将其救下 对其搜魂得知小灵天此地;\r\n识覆天居士 为得八足魔蜥决定助白家解决矿脉魔兽;\r\n参加赵家的祭祖典礼;\r\n与覆天居士、白皙蛟、栾龙天君、寒其子进入幻啸沙漠 灭杀矿脉魔灵 驱逐合体后期魔兽;\r\n返回幻夜城 得八足魔蜥、变异异魔金、金鲸珠、阴罗盘;\r\n从万奴塔处购得魔晶傀儡炼制术并加以研究;\r\n其他九人赶到 汇合;\r\n林家长老因伤重决定退出 韩立委托林家长老带朱果儿返回灵界;\r\n九人进入幻啸沙漠;\r\n赵家雇兽尊殿在出口拦截"},{timestamp:"2338年",content:"走出幻啸沙漠 分头赶往魔源海;\r\n韩立与叶家长老一路 遇兽尊殿拦截 击退;\r\n助叶家长老抽取青翼族人的青鸾真血 分得真血"},{timestamp:"2339年",content:"与其他人汇合 进入魔源海苦灵岛;\r\n被元魇袭击 紫言鼎被毁 苦苦抵挡元魇进攻;\r\n在灵王附体的伪仙儡自爆下逃过一劫 但魔气战甲被毁;\r\n以惊人战力逼退宝花 以炼魔草与银色莲蓬为条件换取进入洗灵池机会;\r\n浸泡洗灵池 服用净灵莲 服用虚灵丹 成功洗经易髓;\r\n神秘小瓶异变;\r\n被涅槃化身所雇佣的蟹道人攻击 使用化劫傀儡、玄天斩灵剑 依然元气大伤;\r\n反雇佣蟹道人灭杀涅槃化身;\r\n拐走蟹道人;\r\n前往蓝瀑湖寻找血牙米以及变异异魔金;\r\n路上靠蟹道人解决众多为灭仙令奖赏而来的魔尊"},{timestamp:"2340年",content:"伤势恢复 重伤血光 名震魔界;\r\n到达蓝瀑湖 大量收购异魔金;\r\n在广源斋遇紫灵 诉相思之苦;\r\n从蓝颖处得知大量购买血牙米的方法、不知名绿气乃阴阳大五行真光附带的地煞阴气;\r\n从无忧手中购得变异异魔金 从其手下处得知私矿点位置 前往开采异魔金 抓阴阳大五行真光的本体 战磁光兽 斩兽得魔核;\r\n购买血牙米;\r\n在蓝颖帮助下得炼化地煞阴气方法;\r\n寻路回灵界;\r\n为得去小灵天方法 出手相助被围困的宝花;\r\n在蟹道人帮助下返回灵界木族领地 一路疾行前往人族 被元刹拦截 敖啸出手相助 时隔两千多年再遇银月;\r\n拒绝敖啸所提迎娶银月的建议 但为帮银月摆脱《忘情诀》决定修炼敖啸自创秘术;\r\n到达木棉城 结识莫简离;\r\n参加木族大战 与夜叉族妃筱汐和木族草疾守卫二号阵眼 与蟹道人合力灭杀十数名圣祖化身 但此战败于魔族"},{timestamp:"2341年",content:"与敖啸、莫简离拜别;\r\n返回人族 寻地闭关 修炼敖啸所传秘术"},{timestamp:"2342年",content:"将敖啸所教秘术炼至大成;\r\n开始炼化异魔金中能量"},{timestamp:"2344年",content:"达到合体后期大圆满境界"},{timestamp:"2345年",content:"巩固境界;\r\n服食血牙米 修炼《百脉炼宝决》"},{timestamp:"2385年",content:"《百脉炼宝决》修炼大成;\r\n炼化青鸾真血;\r\n修炼《炼神术》第二层"},{timestamp:"2395年",content:"《炼神术》第二层修炼而成;\r\n炼制阴阳大五行极山;\r\n重新参悟《青元剑诀》"},{timestamp:"2400年",content:"重新参悟《梵圣真魔功》"},{timestamp:"2411年",content:"进阶大乘;\r\n噬金虫再次变异;\r\n元婴离体神游万里 遇见在空间裂缝养伤的樊咆子 被邀参加广灵道果大会;\r\n到达天渊城 解决圣岛使者杜宇"},{timestamp:"2412年",content:"举办大乘庆典 重创夜叉族黑枭王;\r\n从圣岛使者李蓉处得知灵界大乘们的下落;\r\n在许家救血魂 与血魂商定交易;\r\n入圣岛 换取《祭雷术》下半部;\r\n为救莫简离与敖啸进入魔界;\r\n在黑葫城打探消息;\r\n识邪莲圣祖;\r\n与其他各界大乘参加宝花组织的集会 决定共往始印之地;\r\n答应相助宝花得神胶丹;\r\n进入始印之地 与邪莲、绿石修复法阵;\r\n情况突变 放弃原计划 与宝花合力击伤螟虫之母元神 银纱女子趁机彻底占据螟母躯体 与宝花、蟹道人助何康灭杀银纱女子;\r\n得螟虫之母晶核精华;\r\n暂拒何康交易 得何康赠送的紫芸之精"},{timestamp:"2413年",content:"因始印之地一战 名声渐起;\r\n助宝花缠住元魇;\r\n重返广源斋 见紫灵 知其向往大道的心意 尽力传授魔功修炼心得;\r\n在蓝颖帮助下得知泣灵秘藏所在地;\r\n噬金虫吞噬螟母晶核;\r\n得泣灵秘藏——墨灵圣舟、烈煞金罡沙、近十万魔晶傀儡;\r\n返回灵界"},{timestamp:"2414年",content:"至圣岛;\r\n见敖啸、莫简离 知敖啸天劫将至 从莫简离处得知灵王拥有三清雷霄符;\r\n赠宝予徒弟;\r\n噬金虫王出世;\r\n前往寻找上古祭坛 收花石 寻司南四方坛;\r\n推测小灵天入口在其他大陆"},{timestamp:"2415年",content:"为得三清雷霄符前往灵族;\r\n与灵王达成协议;\r\n与莫简离、血燃和黑鳞前往小修罗界 斩杀不知名龟兽得玄武真血;\r\n与其他三人汇合后大战修罗蛛 击伤五光族奕姓老者 得元魂灯 奕姓老者被已领悟时空法则的合体期修罗蛛樱儿斩杀;\r\n忌惮樱儿的时空法则 与修罗蛛达成协议——助修罗蛛打捞空鱼族圣物镂灵石 换取修罗蛛晶核;\r\n与修罗蛛交换材料;\r\n噬灵火鸟吞噬寒气时 打捞到空鱼族圣器洞天之宝——山海珠;\r\n在空鱼族帮助下祭炼山海珠 被空鱼族五百一二口侍奉为主 将空鱼族带出小修罗界;\r\n与灵王交换得三清雷霄符;\r\n炼化玄武真血;\r\n收空鱼族蓝药为徒"},{timestamp:"2416年",content:"返回天渊 得知敖啸即将渡劫消息 火速赶往圣岛 无奈为时已晚;\r\n在无涯海元合岛开辟洞府 取名青元宫;\r\n为解决银月的《忘情诀》隐患 将芝仙灵躯赠予银月"},{timestamp:"2419年",content:"助银月解决《忘情诀》隐患 携朱果儿、花石往寻小灵天入口;\r\n到许家接冰魄血魂 欲助其寻找本体"},{timestamp:"2421年",content:"途经飞灵族 遇飞灵族大乘越隆 助其炼制极元晶 得五种属性极元晶;\r\n前往拜访元瑶 遇青元子渡劫 斩杀前来寻仇的不灭天尊、黄元子;\r\n参加赫连商盟拍卖会 拍下金阙玉书内页——《五藏锻元功》、跨大陆传送名额;\r\n助真龙族田飞儿到达血天大陆以躲避商盟追查;\r\n答应碧影替赫连商盟出战 问得血天大陆上古祭坛位置;\r\n一路寻找上古祭坛 进入血鹤城;\r\n结识萧冥、万花、清平;\r\n血魂恢复全部记忆 得知冰魄在天鼎宫"},{timestamp:"2422年",content:"带血魂进入天鼎宫 兵分两路;\r\n救出冰魄 获得天鼎真人的提炼雷电之术;\r\n二人被萧冥、万花、清平、华西、无垢堵住 大战起 无垢趁机击杀华西后退走 萧冥三人眼见事不可成 赔礼后退走;\r\n出天鼎宫 与冰魄分开;\r\n携花石、朱果儿寻找上古祭坛 推算小灵天入口"},{timestamp:"2423年",content:"推算入口无果;\r\n应碧影之邀至小世界 结识文心凤"},{timestamp:"2424年",content:"结识赫连商盟雷鸣大陆负责人雷元 结识血煞;\r\n斩杀凶司王 助赫连商盟赢得赌战 获得仙界秘术——《元罡罩》;\r\n寻得小灵天入口坐标;\r\n马良肆虐血天大陆;\r\n救涂咬 在涂咬帮助下找到坐标 进入小灵天;\r\n一千多年后再见南宫婉 互诉别来衷肠;\r\n派花石收集小灵天材料;\r\n携南宫婉或斩杀或降服小灵天强大异族"},{timestamp:"2425年",content:"将南宫婉与部分人族带至灵界;\r\n听闻马良肆虐的消息 忧心忡忡;\r\n返回风元大陆 见明尊 答应联手"},{timestamp:"2426年",content:"返回人族 见莫简离 商讨如何在马良淫威下保存人妖两族;\r\n返回青元宫 带南宫婉游览无涯海;\r\n闭关 提炼辟邪神雷;\r\n收到明尊消息 前往鸣煞之地 识银罡子、化影、乌灵、云淡月梳等顶尖大乘;\r\n负责镇守阵眼 从蟹道人处得知明尊欲献祭自己的意图 金童在蟹道人指挥下修改法阵;\r\n再见冰凤、六翼 轻松擒住六翼 让其替己收集材料以换取自由之身 为二人抹去马良所下的神魂印记;\r\n斩杀阳鹿 得真魂丹;\r\n灵界众强者围攻马良 反被马良全部斩杀;\r\n明尊自爆法阵重创马良;\r\n为得真魂丹战马良 将马良斩杀 得两颗真魂丹、盗天瓶 与火须子及魔光签订天魔契约"},{timestamp:"2428年",content:"将马良与众灵界强者同归于尽的消息传出;\r\n将一颗真魂丹出售给灵界神通最顶尖的八人 此次一役奠定灵界第一大乘身份"},{timestamp:"2430年",content:"将盗天瓶融入掌天瓶中 可驱使掌天瓶部分神通;\r\n前往灵族 见灵王 搜魂骷髅哥 得知掌天瓶瓶灵下落"},{timestamp:"2431年",content:"前往魔界苦灵岛 收服瓶灵;\r\n再见田飞儿 到达龙岛 收购物品 参加各种交换会"},{timestamp:"2432年",content:"用真魂丹与金龙大长老交换龙族其他前辈的渡劫经验以及万年金桑液;\r\n广灵道果大会开始 夺得一枚广灵道果并服下"},{timestamp:"2433年",content:"返回青元宫 闭关"},{timestamp:"2633年",content:"人妖两族实力逐步增强 领地范围扩大倍许"},{timestamp:"2833年",content:"前往魔界始印之地 见何康 求《炼神术》第三层修炼之法 答应修成后助其激发宝符"},{timestamp:"3133年",content:"得白果儿孝敬的昊阴之石;\r\n炼制昊阴寒魄极山"},{timestamp:"3134年",content:"分魂下界 大晋内乱 正邪大战;\r\n送昆吾山重归虚无;\r\n见凌玉灵 得知昔日旧识下落;\r\n游历曾经之地 物是人非;\r\n取出坠魔谷中的北极元晶"},{timestamp:"11134年",content:"人族已是一流势力;\r\n《炼神术》第三层、《五藏锻元功》、《元罡罩》尽皆修成 元合五极山亦炼制成功;\r\n渡飞升之劫进入真仙界北寒仙域！"}],m={data:function(){return{reverse:!1,activities:e}}},i=(r(614),r(47)),s=Object(i.a)(m,(function(){var n=this,t=n.$createElement,r=n._self._c||t;return r("div",{staticClass:"container"},[r("el-timeline",{attrs:{reverse:n.reverse}},n._l(n.activities,(function(t,e){return r("el-timeline-item",{key:e,attrs:{timestamp:t.timestamp}},[n._v("\n      "+n._s(t.content)+"\n    ")])})),1)],1)}),[],!1,null,"18b05043",null);t.default=s.exports}}]);